mapper.select:
SELECT COUNT(*)
FROM board
WHERE menu_id = #{menuId}

mapper.insert:
INSERT INTO board(
    menu_id
    , title
    , content
    , writer
    , password
)
VALUES (
    #{menuId}
    , #{title}
    , #{content}
    , #{writer}
    , #{password}
)

mapper.update:
UPDATE board
SET title = #{boardRequest.title}
    , content = #{boardRequest.content}
    , writer = #{boardRequest.writer}
    , password = #{boardRequest.password}
WHERE id = #{boardId}
